jQuery(document).ready(function($){initModal1();function initModal1(){var modal1_storage_key='modal1_user_already_seen';var modal1=$('.modal1');initCheckAndOpenModal1();$(document).on('click','.modal1Open, [href="#modal1Open"]',function(e){e.preventDefault();openModal1()});$(document).on('click','.modal1-close',function(e){e.preventDefault();closeModal1()});$(document).on('click','.modal1-button',function(e){setStorageModal1()});function openModal1(){modal1.addClass('is-open');$('body').addClass('modal1-is-open');if(window.innerWidth>1023){$('.main-site-header').css('top',modal1.innerHeight()+'px')}}
function closeModal1(){setStorageModal1();modal1.removeClass('is-open');$('body').removeClass('modal1-is-open');if(window.innerWidth>1023){$('.main-site-header').css('top',0)}}
function setStorageModal1(){sessionStorage.setItem(modal1_storage_key,1)}
function initCheckAndOpenModal1(){$(window).on('load',function(){if(!sessionStorage.getItem(modal1_storage_key)){openModal1()}})}}})