jQuery(document).ready(function($){function InitSmoothScrollToAnchor(){$('a[href*="#"]:not([href="#"])').click(function(){if(location.pathname.replace(/^\//,'')===this.pathname.replace(/^\//,'')&&location.hostname===this.hostname){var target=$(this.hash);target=target.length?target:$('[name='+this.hash.slice(1)+']');if(target.length){$('html,body').animate({scrollTop:target.offset().top-70},1000);return!1}}})}
function initGaEvents(){document.addEventListener('wpcf7mailsent',function(event){sendCF7Event(event,'sent')},!1);document.addEventListener('wpcf7invalid',function(event){sendCF7Event(event,'invalid')},!1);document.addEventListener('wpcf7mailfailed',function(event){sendCF7Event(event,'failed')},!1);function sendCF7Event(event,event_action){var formName=$(event.target).find("form").attr("name");if(formName){if(typeof dataLayer==="object"){dataLayer.push({'event':'eventTracking','category':'Forms','action':event_action,'label':formName})}else{gtag('event','contact_form_7',{'event_category':'Forms','event_label':formName,'event_action':event_action})}}}}
function initBlogCTA(){if($('.blog-ctaWithSlider__slider').length){$('.blog-ctaWithSlider__slider').slick({infinite:!0,slidesToShow:1,slidesToScroll:1,dots:!1,arrows:!0,fade:!0,autoplay:!1,})}
$('.js-blog-ctaWithVideo-play').click(function(){$(this).siblings('img').addClass('hide-img');$(this).addClass('hide-img');var videoEl=$(this).siblings('.blog-ctaWithVideo__video');var videoElIframe=videoEl.children('iframe');videoElIframe.addClass('firameeee');videoEl.addClass('show-video');videoElIframe.attr('src',videoElIframe.attr('data-src'))})}
function initAddContactForm7Classes(){document.addEventListener('wpcf7invalid',function(event){var $form=$(event.target).find('form');$form.find('.wpcf7-response-output').addClass('wpcf7-validation-errors').removeClass('wpcf7-mail-sent-ok')},!1);document.addEventListener('wpcf7spam',function(event){var $form=$(event.target).find('form');$form.find('.wpcf7-response-output').addClass('wpcf7-validation-errors').removeClass('wpcf7-mail-sent-ok')},!1);document.addEventListener('wpcf7mailfailed',function(event){var $form=$(event.target).find('form');$form.find('.wpcf7-response-output').addClass('wpcf7-validation-errors').removeClass('wpcf7-mail-sent-ok')},!1);document.addEventListener('wpcf7mailsent',function(event){var $form=$(event.target).find('form');$form.find('.wpcf7-response-output').addClass('wpcf7-mail-sent-ok').removeClass('wpcf7-validation-errors')},!1)}
function initWPCF7Loader(){$('.wpcf7-submit').on('click',function(){var $form=$(this).closest('form');$form.addClass('form-loading')});document.addEventListener('wpcf7submit',removeLoading,!1);document.addEventListener('wpcf7invalid',removeLoading,!1);document.addEventListener('wpcf7mailsent',removeLoading,!1);document.addEventListener('wpcf7mailfailed',removeLoading,!1);function removeLoading(event){var $form=$(event.target).find('form');$form.removeClass('form-loading')}}
initUTMs();initPassUTMsToCalendy();initHSForms();function initUTMs(){window.Utms_Manager={utms:['utm_source','utm_medium','utm_campaign','utm_term','utm_content',]};Utms_Manager.getUTMsFromCookies=function(){var utms_in_cookies={};Utms_Manager.utms.forEach(function(utm_key){var utm_val=Cookies.get(utm_key);if(utm_val){utms_in_cookies[utm_key]=utm_val}});return utms_in_cookies}
Utms_Manager.ifUserCameWithUTMs=function(){var exists=!1;Utms_Manager.utms.forEach(function(utm){var url_param_value=getUrlParam(utm);if(url_param_value){exists=!0}});return exists}
Utms_Manager.initSaveUTMsToCookies=function(){var UserCameWithUTMs=Utms_Manager.ifUserCameWithUTMs();Utms_Manager.utms.forEach(function(utm){var url_param_value=getUrlParam(utm);if(url_param_value){Cookies.set(utm,url_param_value,{expires:365,domain:window.location.host,path:'/'})}else if(!url_param_value&&UserCameWithUTMs){Cookies.remove(utm,{domain:window.location.host,path:'/'})}})}
Utms_Manager.populateUTMs=function(){var utms_in_cookies=Utms_Manager.getUTMsFromCookies();if(utms_in_cookies){for(var utm_key in utms_in_cookies){if(Object.prototype.hasOwnProperty.call(utms_in_cookies,utm_key)){var utm_val=utms_in_cookies[utm_key];$('[name="'+utm_key+'"]').val(utm_val)}}
$('.js-add-utms').each(function(item){var currentURL=$(this).attr('href');var newURL=addParamsToUrl(currentURL,utms_in_cookies);$(this).attr('href',newURL)})}}
Utms_Manager.init=function(){Utms_Manager.initSaveUTMsToCookies();Utms_Manager.populateUTMs()}
Utms_Manager.init()}
function initPassUTMsToCalendy(){var utms_in_cookies=Utms_Manager.getUTMsFromCookies();$('a[href*="/calendly"]').each(function(){var currentURL=$(this).attr('href');var newURL=addParamsToUrl(currentURL,utms_in_cookies);$(this).attr('href',newURL)})}
function initHSForms(){window.addEventListener('message',event=>{if(event.data.type==='hsFormCallback'&&event.data.eventName==='onFormReady'){$(document).trigger('hubspot_forms_loaded')}
if(event.data.type==='hsFormCallback'&&event.data.eventName==='onFormSubmit'){$(document).trigger('hubspot_form_submit',{status:'success',formId:event.data.id,formName:tg.getHubSpotFormName(event.data.id),formData:event.data})}});$(document).on('hubspot_forms_loaded',function(e,eventInfo){setTimeout(function(){addUTMsToHSForms()},500)});function addUTMsToHSForms(){tg.globals.utms.forEach(function(utm_key){var utm_val=Cookies.get(utm_key);if(utm_val){$('[name="'+utm_key+'"]').val(utm_val)}})}}
initGaEvents();InitSmoothScrollToAnchor();initBlogCTA();initAddContactForm7Classes();initWPCF7Loader();function getUrlParam(name,url){if(!url)url=window.location.href;name=name.replace(/[\[\]]/g,"\\$&");var regex=new RegExp("[?&]"+name+"(=([^&#]*)|&|#|$)"),results=regex.exec(url);if(!results)return null;if(!results[2])return'';return decodeURIComponent(results[2].replace(/\+/g," "))}
function addParamsToUrl(href,params){if(href&&validURL(href)&&params){var url=new URL(href);for(var param in params){if(Object.prototype.hasOwnProperty.call(params,param)){url.searchParams.set(param,params[param])}}
return url.href}
return href}
function validURL(str){var pattern=new RegExp('^(https?:\\/\\/)?'+'((([a-z\\d]([a-z\\d-]*[a-z\\d])*)\\.)+[a-z]{2,}|'+'((\\d{1,3}\\.){3}\\d{1,3}))'+'(\\:\\d+)?(\\/[-a-z\\d%_.~+]*)*'+'(\\?[;&a-z\\d%_.~+=-]*)?'+'(\\#[-a-z\\d_]*)?$','i');return!!pattern.test(str)}})