jQuery(document).ready(function($){const $header=$('.section-header');const $toggler=$('.section-header-toggler');const $menu=$('.section-header__popover');const $params={scrollTop:null,windowWidth:null,menuOpened:!1,isTopOfPage:!1,isTablet:!1,screens:{mobile:320,tabletPortrait:768,tabletLandscape:992,desktop:1200}}
initMenuToggler();initMobileDropdowns();initPreventMenuItemClickJumping();initMenuItemsEqualHeight();$(window).on('resize load',function(){initPCDropdowns()});$(window).on('resize scroll load',function(){initStatesChange();initHeaderEvents()});function initPCDropdowns(){const sectionEls=document.querySelectorAll('.section-header__content');const dropdownLink=document.querySelectorAll('.dropdown-menu-item');const navNormalLink=document.querySelectorAll('.section-header__nav-menuLink:not(.dropdown-menu-item)');const popoverEl=document.querySelector('.section-header__popover');const contentEl=document.querySelector('.section-header__dropdown');const headerNav=$('.section-header__nav');const arrowEl=document.querySelector('.section-header__popover-arrow');const backgroundEl=document.querySelector('.section-header__popover-bg');const dimensions={};const popoverLeft=popoverEl.getBoundingClientRect().x;dropdownLink.forEach((navLink)=>{let section=navLink.getAttribute('data-nav');let rect=navLink.getBoundingClientRect();dimensions[section]={};dimensions[section].width=$(`.section-${section}`).innerWidth();dimensions[section].height=$(`.section-${section}`).innerHeight();dimensions[section].arrowX=rect.left+(rect.width/2)-popoverLeft});arrowEl.style.transform=`
      translateX(${dimensions.menu1.arrowX}px)
      rotate(45deg) skew(6deg, 6deg)`;if($params.windowWidth>=$params.screens.tabletLandscape){contentEl.style=''}
function showDropdown(section){popoverEl.classList.add('open');sectionEls.forEach(el=>el.classList.remove('active'));document.querySelector(`.section-${section}`).classList.add('active');arrowEl.style.transform=`
        translateX(${dimensions[section].arrowX}px)
        rotate(45deg) skew(6deg, 6deg)`;backgroundEl.style.height=dimensions[section].height+'px';backgroundEl.style.width=dimensions[section].width+'px';if($params.windowWidth>=$params.screens.tabletLandscape){contentEl.style.width=dimensions[section].width+'px';contentEl.style.height=dimensions[section].height+'px'}}
dropdownLink.forEach((navLink)=>{navLink.addEventListener('mouseenter',(event)=>{let targetPopover=event.target.getAttribute('data-nav');showDropdown(targetPopover)})});navNormalLink.forEach((navLink)=>{navLink.addEventListener('mouseenter',(event)=>{popoverEl.classList.remove('open')})});headerNav.on('mouseleave',()=>{popoverEl.classList.remove('open')})}
function initMobileDropdowns(){$('.dropdown-toggler').on('click',function(){let $currentDropdown=$(this).parents('.section-header__content');if($currentDropdown.hasClass('dropdown-opened')){$currentDropdown.removeClass('dropdown-opened')}else{$('.dropdown-opened').removeClass('dropdown-opened');$currentDropdown.addClass('dropdown-opened');$('.section-header__dropdown').animate({scrollTop:0})}})}
function initHeaderEvents(){if(!$params.isTopOfPage){$header.addClass('is-scrolled')}else{$header.removeClass('is-scrolled')}
if($params.menuOpened&&$params.windowWidth<$params.screens.tabletLandscape){$('html').addClass('html-fixed')}else if(!$params.menuOpened){$('html').removeClass('html-fixed')}}
function initMenuToggler(){$toggler.on('click',(e)=>{e.preventDefault();$params.menuOpened=!$params.menuOpened;if($params.menuOpened){$toggler.addClass('active');$menu.addClass('opened');$header.addClass('is-opened')}else{$toggler.removeClass('active');$menu.removeClass('opened');$header.removeClass('is-opened')}
initHeaderEvents()})}
function initStatesChange(){$params.scrollTop=$(window).scrollTop();$params.windowWidth=$(window).width();$params.isTopOfPage=!$params.scrollTop;$params.isTablet=$params.windowWidth<$params.screens.tabletLandscape;if(!$params.isTablet){if($params.menuOpened){$params.menuOpened=!1}
$toggler.removeClass('active');$menu.removeClass('opened');$header.removeClass('is-opened')}}
function initPreventMenuItemClickJumping(){$('.dropdown-menu-item a').on('click',function(e){e.preventDefault()})}
function initMenuItemsEqualHeight(){if(window.innerWidth<992){return}
$('.section-header__content').each(function(){var $menuItem=$(this).find('.section-header__contentCol-listItem');var highest=0;$menuItem.each(function(){highest=Math.max(highest,$(this).height())});$menuItem.height(highest)})}})