jQuery(document).ready(function($){var shares_wrapper_el=$(".social_share_buttons.mobile_sticky");var shares_single_el=$('.social_share_buttons a:not(".prevent_popup")');if(shares_wrapper_el.length){$('html').addClass('social-share-buttons-on')}
shares_single_el.click(function(e){e.preventDefault();var href=$(this).attr('href');var title=$(this).attr('title');PopupCenter(href,title,500,300)});function PopupCenter(url,title,w,h){var dualScreenLeft=window.screenLeft!=undefined?window.screenLeft:screen.left;var dualScreenTop=window.screenTop!=undefined?window.screenTop:screen.top;var width=window.innerWidth?window.innerWidth:document.documentElement.clientWidth?document.documentElement.clientWidth:screen.width;var height=window.innerHeight?window.innerHeight:document.documentElement.clientHeight?document.documentElement.clientHeight:screen.height;var left=((width/2)-(w/2))+dualScreenLeft;var top=((height/2)-(h/2))+dualScreenTop;var newWindow=window.open(url,title,'scrollbars=yes, width='+w+', height='+h+', top='+top+', left='+left);if(window.focus){newWindow.focus()}}})